    def test_UntappdConstructorFail(self):
        with self.assertRaises(ValueError):
            Untappd("Test", "https://untappd.com/v/bar/bad_url")
        with self.assertRaises(ValueError):
            Untappd("Test", "https:/untappd.com/v/bar/134")

    def test_GetABVFromSpan(self):
        self.assertEqual(Untappd.get_abv_from_span("5.6% ABV 2 . 234"), 5.6)
        self.assertEqual(Untappd.get_abv_from_span("1.0% ABV"), 1.0)
        self.assertEqual(Untappd.get_abv_from_span("0.0% ABV dffddf"), 0.0)
        self.assertEqual(Untappd.get_abv_from_span("1.0% IBU"), 0)
        self.assertEqual(Untappd.get_abv_from_span("abc% ABV"), 0)

